import api from "../../api/api.js";

export default {
  data: () => ({
    keyword_count: null,
    keyword_check: false,
    keyword_check_result: "",
    twitter_account: "",
    twitter_account_id: null,
    twitter_account_response: null,
    twitter_account_check: true,
    twitter_account_result: null,
    tweet_url: "",
    tweet_id: null,
    tweet_url_response: null,
    tweet_url_check: true,
    tweet_url_result: null,
  }),
  methods: {
    checkKeywordPromise: function () {
      return new Promise((resolve) => {
        api
          .checkKeyword({
            keyword: this.keyword,
          })
          .then((response) => {
            this.keyword_count = response["data"];
            if (this.keyword_count < 5000) {
              this.keyword_check = true;
              this.keyword_check_result =
                "指定されたキーワードの直近７日間の利用数は" +
                String(this.keyword_count) +
                "回であり、利用可能です。";
            } else {
              this.keyword_check = false;
              this.keyword_check_result =
                "指定されたキーワードの直近７日間の利用数は" +
                String(this.keyword_count) +
                "回であり、利用数が多すぎます。別のキーワードをご利用ください";
            }
            resolve("resolved");
          })
          .catch((error) => {
            this.$toast.show("キーワードのチェックに失敗しました", {
              type: "error",
              position: "top-right",
              duration: 5000,
            });
            console.log("error: ", error);
            resolve("rejected");
          });
      });
    },
  },
};
