import { createRouter, createWebHistory } from "vue-router";
import HomeView from "../views/HomeView.vue";
import CampaignListsView from "../views/CampaignLists.vue";
import CampaignCreateView from "../views/CampaignCreate.vue";
import CampaignDetailView from "../views/CampaignDetail.vue";
import LoginView from "../views/LoginPage.vue";
import SignupView from "../views/SignupPage.vue";
import PaymentHistoryView from "../views/PaymentHistory.vue";
import PlansView from "../views/SubscribePlans.vue";
import MailSentView from "../views/MailSent.vue";
import PasswordResetView from "../views/PasswordReset.vue";
import ModifyCampaignView from "../views/CampaignModify.vue";
import CampaignAnalisysView from "../views/CampaignAnalitics";
import CampaignAddOptionsView from "../views/CampaignAddOptions";
// import InstantwinCampaignView from "../views/InstantwinCampaign";
import AdminLoginView from "../views/AdminLogin";
import AdminCampaignView from "../views/AdminCampaignList";
import AdminModifyCampaignView from "../AdminCampaignModify";
import AdminCampaignAnalisysView from "../AdminCampaignAnalitics.vue";
import AboutUsView from "../views/AboutUs";
import store from "@/store";

const routes = [
  // {
  //   path: "/instantwin/campaigns",
  //   name: "instantwinCampaigns",
  //   component: InstantwinCampaignView,
  //   meta: { requiresAuth: false },
  // },
  {
    path: "/admin/login",
    name: "adminLogin",
    component: AdminLoginView,
    meta: { requiresAuth: false },
  },
  {
    path: "/admin/campaigns",
    name: "adminCampaigns",
    component: AdminCampaignView,
    meta: { requiresAuth: false },
  },
  {
    path: "/admin/campaigns/analisys/:id",
    name: "adminAnalisys",
    component: AdminCampaignAnalisysView,
    meta: { requiresAuth: false },
  },
  {
    path: "/admin/campaigns/modify/:id",
    name: "adminModifyCampaign",
    component: AdminModifyCampaignView,
    meta: { requiresAuth: false },
  },

  {
    path: "/campaigns/analisys/:id",
    name: "analisys",
    component: CampaignAnalisysView,
    meta: { requiresAuth: true },
  },
  {
    path: "/campaigns/add-options/:id",
    name: "addOptions",
    component: CampaignAddOptionsView,
    meta: { requiresAuth: true },
  },
  {
    path: "/about-us",
    name: "about",
    component: AboutUsView,
    meta: { requiresAuth: false },
  },
  {
    path: "/campaigns/modify/:id",
    name: "modifyCampaign",
    component: ModifyCampaignView,
    meta: { requiresAuth: true },
  },
  {
    path: "/reset-password",
    name: "resetPassword",
    component: PasswordResetView,
    meta: { requiresAuth: false },
  },
  {
    path: "/sent-mail",
    name: "mailsent",
    component: MailSentView,
    meta: { requiresAuth: false },
  },
  {
    path: "/campaigns",
    name: "campaigns",
    component: CampaignListsView,
    meta: { requiresAuth: true },
  },
  {
    path: "/sign-up",
    name: "signUp",
    component: SignupView,
  },
  {
    path: "/campaign",
    name: "campaign",
    component: CampaignDetailView,
    meta: { requiresAuth: true },
  },
  {
    path: "/create-campaign",
    name: "createCampaign",
    component: CampaignCreateView,
    meta: { requiresAuth: true },
  },
  {
    path: "/login",
    name: "logIn",
    component: LoginView,
    beforeEnter: (to, from, next) => {
      if (store.getters["user"]) {
        next({
          path: "/campaigns",
        });
      } else {
        next();
      }
    },
  },
  {
    path: "/payment-history",
    name: "paymentHistory",
    component: PaymentHistoryView,
    meta: { requiresAuth: true },
  },
  {
    path: "/subscribe",
    name: "subscribePlans",
    component: PlansView,
    meta: { requiresAuth: true },
  },
  {
    path: "/",
    name: "home",
    component: HomeView,
    meta: { requiresAuth: false },
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

router.beforeEach((to, from, next) => {
  const requiresAuth = to.matched.some((record) => record.meta.requiresAuth);
  if (requiresAuth) {
    if (!store.getters["user"]) {
      next({
        path: "/login",
        query: { redirect: to.fullPath },
      });
    } else {
      next();
    }
  } else {
    next();
  }
});

export default router;
