import axios, { AxiosRequestConfig } from "axios";
import mock from "./mock/api";
import { getAuth } from "@firebase/auth";

let config = {
  headers: {
    Authorization: null,
  },
};

export const client = axios.create({
  baseURL: "http://127.0.0.1:8000",
});

client.interceptors.request.use(async (request) => {
  const user = getAuth().currentUser;
  const token = await user.getIdToken(true); //force update= true
  config.headers["Authorization"] = token;
  return request;
});

// モック起動
if (JSON.parse(process.env.VUE_APP_USE_MOCK)) {
  mock.run(client);
}

export default {
  /**
   * Todoリスト取得API
   * 結果はPromiseを返してハンドリングする
   * @param {Object} params パラメータ
   */
  getAccounts(params) {
    return client.get("/accounts", { params: params }, config);
  },
  createAccount(params) {
    return client.post("/accounts", params, config);
  },
  getOrganizers(params) {
    return client.get("/organizers", params, config);
  },
  createOrganizer(params) {
    return client.post("/organizers", params, config);
  },
  getCampaigns(params) {
    return client.get("/campaigns", { params: params }, config);
  },
  createCampaign(params) {
    return client.post("/campaigns", params, config);
  },
};
