import axios from "axios";
import mock from "./mock/api";
import {
  getAuth,
  sendEmailVerification,
  signInWithEmailAndPassword,
} from "@firebase/auth";

export const client = axios.create({
  baseURL: "http://127.0.0.1:8000",
});

// モック起動
if (JSON.parse(process.env.VUE_APP_USE_MOCK)) {
  mock.run(client);
}

axios.interceptors.request.use((request) => {
  const user = getAuth().currentUser();
  return request;
});

export default {
  /**
   * Todoリスト取得API
   * 結果はPromiseを返してハンドリングする
   * @param {Object} params パラメータ
   */
  getAccounts(params) {
    return client.get("/accounts", { params: params });
  },
  createAccount(params) {
    return client.post("/accounts", params);
  },
  getOrganizers(params) {
    return client.get("/organizers", params);
  },
  createOrganizer(params) {
    console.log(params);
    return client.post("/organizers", params);
  },
  getCampaigns(params) {
    console.log(params);
    return client.get("/campaigns", { params: params });
  },
  createCampaign(params) {
    console.log(params);
    return client.post("/campaigns", params);
  },
};
