import axios from "axios";
import mock from "./mock/api";
import store from "@/store";

let base = "";
if (JSON.parse(process.env.VUE_APP_ENDPOINT_USE_PROD)) {
  base = "https://social-marketing.io/api/v1/fjiophdfa33";
} else {
  base = "http://127.0.0.1:8000/api/v1/fjiophdfa33";
}
export const client = axios.create({
  baseURL: base,
});

client.interceptors.request.use(async (config) => {
  const token = store.getters["adminKey"];
  config.headers = { Authorization: token };
  return config;
});

// モック起動
if (JSON.parse(process.env.VUE_APP_USE_MOCK)) {
  mock.run(client);
}

export default {
  getAccounts(params) {
    return client.post("/login", { params: params });
  },
};
