import axios from "axios";
import mock from "./mock/api";
import store from "@/store";
// import "../firebase_config";
// import { initFirebaseAuth } from "../firebase_config";

let base = "";
if (JSON.parse(process.env.VUE_APP_ENDPOINT_USE_PROD)) {
  base = "https://social-marketing.io/api/v1";
} else {
  base = "http://127.0.0.1:8081/api/v1/instantwins";
}
export const client = axios.create({
  baseURL: base,
});

client.interceptors.request.use(async (config) => {
  const twitterId = store.getters["userId"].split("/")[3];
  const twitterAccessToken = store.getters["oauthAccessToken"];
  const twitterAccessSecret = store.getters["oauthTokenSecret"];
  // console.log(twitterId.split("/")[3]);
  console.log(twitterId);
  console.log(twitterAccessToken);
  console.log(twitterAccessSecret);
  config.headers = {
    twitter_id: twitterId,
    access_token: twitterAccessToken,
    access_secret: twitterAccessSecret,
  };
  return config;
});

// モック起動
if (JSON.parse(process.env.VUE_APP_USE_MOCK)) {
  mock.run(client);
}

export default {
  getHealth(params) {
    return client.get("/", params);
  },
  postUser(params) {
    return client.post("/user", params);
  },
};
