import axios from "axios";
import mock from "./mock/api";
import store from "@/store";

export const client = axios.create({
  baseURL: "http://127.0.0.1:8000",
  headers: {
    Authorization: "",
  },
});

client.interceptors.request.use(async (config) => {
  // console.log("interceptor ", token);
  const token = await store.getters["idToken"];
  console.log(token);
  console.log(config);
  // axios.defaults.headers.common["Authorization"] = token;
  // request.headers.common["Authorization"] = token;
  return config;
});

// モック起動
if (JSON.parse(process.env.VUE_APP_USE_MOCK)) {
  mock.run(client);
}

export default {
  getAccounts(params) {
    console.log("get accounts");
    return client.get("/accounts", { params: params });
  },
  createAccount(params) {
    return client.post("/accounts", params);
  },
  getOrganizers(params) {
    console.log("get organizers");
    return client.get("/organizers", params);
  },
  createOrganizer(params) {
    return client.post("/organizers", params);
  },
  getCampaigns(params) {
    console.log("get campaigns");
    return client.get("/campaigns", { params: params });
  },
  createCampaign(params) {
    return client.post("/campaigns", params);
  },
};
