import { createRouter, createWebHistory } from "vue-router";
import HomeView from "../views/HomeView.vue";
import CampaignListView from "../views/Campaign_list.vue";
import CampaignResitView from "../views/Campaign_regist.vue";
import CampaignDetailView from "../views/Campaign_detail.vue";
import LoginView from "../views/login_page.vue";
import SignUpView from "../views/SignUp_page.vue";
import PaymentHistoryView from "../views/Payment_history.vue";
import PlansView from "../views/subscribe_plans.vue";

const routes = [
  {
    path: "/campaigns",
    name: "campaigns",
    component: CampaignListView,
    meta: { requiresAuth: true },
  },
  {
    path: "/signUp",
    name: "signUp",
    component: SignUpView,
  },
  {
    path: "/campaign",
    name: "campaign",
    component: CampaignDetailView,
    meta: { requiresAuth: true },
  },
  {
    path: "/createCampaign",
    name: "createCampaign",
    component: CampaignResitView,
    meta: { requiresAuth: true },
  },
  {
    path: "/login",
    name: "login",
    component: LoginView,
  },
  {
    path: "/paymentHistory",
    name: "paymentHistory",
    component: PaymentHistoryView,
    meta: { requiresAuth: true },
  },
  {
    path: "/subscribePlans",
    name: "subscribePlans",
    component: PlansView,
    meta: { requiresAuth: true },
  },
  {
    path: "/",
    name: "home",
    component: HomeView,
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

router.beforeEach((to, from, next) => {
  const requiresAuth = to.matched.some((record) => record.meta.requiresAuth);
  if (requiresAuth) {
    if (!store.getters["user"]) {
      next({
        path: "/signin",
        query: { redirect: to.fullPath },
      });
    } else {
      next();
    }
  } else {
    next();
  }
});

export default router;
