import firebase from "firebase/compat/app";
import { getAuth } from "@firebase/auth";
import store from "@/store";

const config = {
  apiKey: "AIzaSyC89btP_8Wyw9bLBk3QiM8Jos3-z3uFgNc",
  authDomain: "fuelcrypto-d160f.firebaseapp.com",
  projectId: "fuelcrypto-d160f",
  storageBucket: "fuelcrypto-d160f.appspot.com",
  messagingSenderId: "508410262474",
  appId: "1:508410262474:web:7aed7b3356cbc7fc51d533",
  measurementId: "G-5K4ZRT54QZ",
};
firebase.initializeApp(config);
const auth = getAuth();

auth.onAuthStateChanged((user) => {
  store.commit("updateUser", user);
});

export function initFirebaseAuth() {
  return new Promise((resolve) => {
    var unsubscribe = auth.onAuthStateChanged((user) => {
      // user オブジェクトを resolve
      resolve(user);
      // 登録解除
      unsubscribe();
    });
  });
}
